import input_outputimport process_text# file should be in Files folder# replace file to perform the program on ittext_file = "the-full-bee-movie-script.txt"if __name__ == '__main__':    text = input_output.get_text_from_file(text_file)    action_value = 0    keywords = ""    word_occurrence = ""    # main loop of the running program    while action_value != 4:        print("1: Count word\n2: Show keywords graph\n3: Save performed actions data to file\n4: Exit")        print("Write an integer between 1 and 4:")        input_value = input()        if input_value.isdigit():            action_value = int(input_value)        else:            print("Not an integer")        if action_value < 1:            print("Wrong number")        elif action_value == 1:            print("Enter word you want to count:")            word = input()            # save all word occurrences in the file later.            word_occurrence += process_text.count_word(word, text) + "\n"        elif action_value == 2:            keywords = process_text.create_graph(text)        elif action_value == 3:            print("Write the title of the file:")            text_name = input()            text = "TEXT OVERVIEW:\n" + word_occurrence + "\n" + keywords            process_text.save_data(text_name, text)        elif action_value > 4:            print("Wrong number")    print("EXIT")