import input_outputimport nltkimport matplotlib.pyplot as pltimport redef create_graph(text):    count_words = {}    stopwords = nltk.corpus.stopwords.words('english')    # text processing    text = re.sub(r"[,.\";@#?!&$'-]+ *", " ", text)    words = text.lower().split()    # removing repetition    set_of_words = set(words)    # removing not alphapetic strings    set_of_words = [i for i in set_of_words if i.isalpha()]    # words shorter than 3 letters and longer than 12 letters dont match significant words    set_of_words = [i for i in set_of_words if 3 < len(i) < 12]    # removing stopwords    set_of_words = [i for i in set_of_words if i not in stopwords]    for i in set_of_words:        count_words[i] = words.count(i)    # create sorted dictionary with most frequent words    count_words = dict(sorted(count_words.items(), key=lambda item: item[1], reverse=True))    count_words = dict(list(count_words.items())[0: 15])    keywords = "Text keywords: " + str(list(count_words.keys()))    names = list(count_words.keys())    values = list(count_words.values())    plt.bar(range(len(count_words)), values, tick_label=names)    plt.show()    return keywordsdef count_word(word, text):    # pattern to match only the word in parameter (not a part of the string)    pattern = r"\b" + re.escape(word.lower()) + r"\b(?!')"    number = len(re.findall(pattern, text))    word_occurrence = "Word '" + word + "' occurrence " + str(number) + " times in this the text"    print(word_occurrence)    return word_occurrencedef save_data(text_name, text_to_save):    input_output.save_text_in_file("Overview_" + text_name, text_to_save)    print("data saved")