import input_outputimport nltkimport matplotlib.pyplot as pltdef create_graph(movie_text):    count_words = {}    stopwords = nltk.corpus.stopwords.words('english')    words = movie_text.lower().split(" ")    set_of_words = set(words)    temp = []    for i in set_of_words:        if i.isalpha():            temp.append(i)    set_of_words = temp    temp = []    for i in set_of_words:        if 3 < len(i) < 12:            temp.append(i)    set_of_words = temp    temp = []    for i in set_of_words:        if i not in stopwords:            temp.append(i)    set_of_words = temp    for i in set_of_words:        count_words[i] = words.count(i)    count_words = dict(sorted(count_words.items(), key= lambda item: item[1], reverse=True))    count_words = dict(list(count_words.items())[0: 15])    keywords = "Movie keywords: " + str(list(count_words.keys()))    names = list(count_words.keys())    values = list(count_words.values())    plt.bar(range(len(count_words)), values, tick_label=names)    plt.show()    return keywordsdef count_word(word, movie_text):    number = movie_text.count(word)    word_occurrence = "Word '" + word + "' occurrence " + str(number) + " times in this movie"    print(word_occurrence)    return word_occurrencedef save_data(movie_name, text_to_save):    input_output.save_text_in_file("Overview_" + movie_name, text_to_save)    print("data saved")